mapper.select:
select

from s_student_info st


    and st.student_name like concat('%',#{name},'%')
    and st.tocollege = #{tocollegeId}
    and st.tomajor = #{tomajorId}
    and st.class_id = #{classId}


    limit #{offset}, #{limit}

select
    count(*)
from s_student_info st


    and st.student_name like concat('%',#{name},'%')
    and st.tocollege = #{tocollegeId}
    and st.tomajor = #{tomajorId}
    and st.class_id = #{classId}

select

from s_student_info st

where st.id = #{id}

mapper.update:
update s_student_info
student_id = #{studentId}, exam_id = #{examId}, class_id = #{classId}, student_name = #{studentName}, certify = #{certify}, mail_address = #{mailAddress}, foreign_lanaguage = #{foreignLanaguage}, student_sex = #{studentSex}, nation = #{nation}, political = #{political}, card_id = #{cardId}, telephone = #{telephone}, subject_type = #{subjectType}, tocollege = #{tocollege}, tocampus = #{tocampus}, tomajor = #{tomajor}, birthplace = #{birthplace}, grade = #{grade}, isstate = #{isstate}, birthday = #{birthday}, note = #{note}, add_time = #{addTime}, add_userid = #{addUserid}, edit_time = #{editTime}, edit_userid = #{editUserid},
where id = #{id}
